content:
環境設定 ruby -v sudo gem update &ndash;system

// インストール sudo gem install sass
// アップデート gem update sass
// アンインストール gem uninstall sass
// バージョンを指定してインストール gem install sass -v 特定のバージョン
コンパイル sass &ndash;style expended &ndash;watch sass:css
入れ子(ネスト)構造 #main { width: 90%; P { font-size: 16px; a { text-decoration: none; &amp;:hover { font-weight: bold; } } } }
コメント /* commnet */
// comment こちらはCSSには反映されない
変数 $imgDir: &ldquo;../img/&rdquo;; $baseFontSize: 14px; $brandColor: rgba(255,0,0,0.9);
#main { width: 90%; backgoround: url($imgDir + &ldquo;bg.png&rdquo;); // url(&ldquo;#{$imgDir}bg.png&rdquo;); P { color: $brandColor; // lighten, darken // lighten($brandColor, 30%); font-size: $baseFontSize; .sub { font-size: $baseFontSize -2px; // #{12 + 12}px; } } }
条件分岐 $debugMode: true; $x: 10;
#main { // == != &lt; &gt; &lt;= &gt;= @if $debugMode == true { color: red; } @else { color: green; } p { @if $x &gt; 20 {color: yellow;} } }
ループ @for $i from 10 through 14 { .fs{$i} { font-size: #{$i}px; } }
$i: 10; @while $i &lt;= 14 { .fs{$i} { font-size: #{$i}px; } $i: $i + 1; }
リスト $animals: cat, dog, tiger;
@each $animal in $animals { // @each $animal in cat, dog, tiger { .#{$animal}-icon { background: url(&ldquo;#{$animal}.png&rdquo;) }; }
関数の作り方 $totalWidth: 940px; $columnCount: 5;
@function getColumnWidth($width, $count) { // $columnWidthを計算 $padding: 10px; $columnWidth: floor(($width - ($padding * ($count - 1))) / $count); @debug $columnWidth; // コンパイル時点で表示してくれる @return $columnWidth; }
.grid { float: left; width: $getColumnWidth($totalWidth, $columnCount); }
ファイルを分離して管理する @import &ldquo;settings&rdquo;; // _settings.scss @import &ldquo;functions&rdquo;; // _functions.scss
.grid { float: left; width: $getColumnWidth($totalWidth, $columnCount); }
mixin @mixin round($radius:4px) { border-radius: $radius; }
.label { fount-size: 12px; @include round(10px); }
extend(継承) .msg { font-size: 12px; font-weight: bold; padding: 2px 4px; color: white; }
.errorMsg { @extend .msg; background: red; }
.warningMsg { @extend .msg; background: orange; }
